package ch06_3_exer;

public class math {

	int num1;
	int num2;
	
	public math(int num1, int num2) {
		super();
		this.num1 = num1;
		this.num2 = num2;
	}
	public math() {
		super();
		// TODO Auto-generated constructor stub
	}
	
	public void add() {
		System.out.println(this.num1 + this.num2);
	}
	public void minus() {
		System.out.println(this.num1 - this.num2);
	}
	public void mul() {
		System.out.println(this.num1 * this.num2);
	}
	public void divid() {
		System.out.println(this.num1 / this.num2);
	}





}

		
		
	

